// Detect Input Type - Extensible for future multimodal support
const items = $("Read Voice Memo").all()
const results = [];

// File extension to type mapping
const audioExtensions = ['mp3', 'wav', 'flac', 'm4a', 'ogg', 'opus', 'webm', 'aac', 'wma'];
const imageExtensions = ['jpg', 'jpeg', 'png', 'gif', 'webp', 'bmp', 'svg', 'heic'];
const videoExtensions = ['mp4', 'mov', 'avi', 'mkv', 'wmv'];

for (const item of items) {
    const binaryKeys = Object.keys(item.binary || {});

    if (binaryKeys.length === 0) {
        results.push({
            json: {
                inputType: 'none',
                error: 'No binary data received',
                originalData: item.json
            }
        });
        continue;
    }

    const binaryKey = binaryKeys[0];
    const binaryData = item.binary[binaryKey];
    const mimeType = (binaryData.mimeType || '').toLowerCase();
    const fileName = binaryData.fileName || 'unknown';
    const ext = fileName.split('.').pop()?.toLowerCase() || '';

    let inputType = 'unsupported';

    // Check by MIME type first
    // Treat webm as audio (browser MediaRecorder sends audio as video/webm)
    if (mimeType.startsWith('audio/') || mimeType === 'video/webm') {
        inputType = 'audio';
    } else if (mimeType.startsWith('image/')) {
        inputType = 'image';
    } else if (mimeType.startsWith('video/')) {
        inputType = 'video';
    } else if (mimeType === 'application/pdf') {
        inputType = 'pdf';
    }
    // Fallback to file extension if MIME type didn't match
    else if (audioExtensions.includes(ext)) {
        inputType = 'audio';
    } else if (imageExtensions.includes(ext)) {
        inputType = 'image';
    } else if (videoExtensions.includes(ext)) {
        inputType = 'video';
    } else if (ext === 'pdf') {
        inputType = 'pdf';
    }

    results.push({
        json: {
            inputType,
            mimeType,
            fileName,
            extension: ext,
            binaryKey,
            fileSize: binaryData.fileSize || 0,
        },
        binary: item.binary
    });
}

return results;

const items = $input.all();
const results = [];

for (const item of items) {
    const llmData = item.json.output || {};

    const transcript = $('Whisper Transcription').first().json.text || 'No transcript available';
    const rawTitle = llmData.title || 'voice-memo';
    const slug = rawTitle.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-+|-+$/g, '').substring(0, 50);

    const now = new Date();
    const date = now.toISOString().split('T')[0];
    const isoTimestamp = now.toISOString();
    const duration = $("ffprobe").first().json.stdout
    const readableDate = now.toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' });

    // Get original filename from the source
    const originalFilename = $('Detect Input Type').first().json.fileName || 'unknown';
    const extension = $('Detect Input Type').first().json.extension || 'mp3';

    const fileHash = $('Crypto').first().json.fileHash?.substring(0, 8) || Date.now().toString(16);
    const filename = `${date}-${slug}-${fileHash}.md`;
    const filepath = `/data/notes/${filename}`;
    const archivePath = `/data/audio-archive/${date}-${slug}-${fileHash}.${extension}`;

    const allTags = ['memo', ...(llmData.tags || [])];
    const tagsYaml = allTags.map(t => `  - ${String(t).toLowerCase().replace(/\s+/g, '-')}`).join('\n');

    const keyPoints = (llmData.key_points || []).map(p => `- ${p}`).join('\n') || '- No key points identified';
    const actionItems = (llmData.action_items || []).map(ai => `- [ ] ${ai}`).join('\n') || '- No action items identified';
    const questions = (llmData.questions || []).map(q => `- ${q}`).join('\n') || '- No questions identified';

    const markdown = [
        '---',
        `title: "${(llmData.title || 'Voice Memo').replace(/"/g, '\\"')}"`,
        `date: ${isoTimestamp}`,
        'type: voice-memo',
        `duration: ${duration}`,
        `original_filename: "${originalFilename}"`,
        `audio_archive: "[[audio-archive/${date}-${slug}-${fileHash}.${extension}]]"`,
        'tags:',
        tagsYaml,
        'status: processed',
        '---',
        '',
        `# ${llmData.title || 'Voice Memo'}`,
        '',
        `**Date:** ${readableDate}`,
        '**Type:** Voice Memo',
        '',
        '---',
        '',
        '## Summary',
        '',
        llmData.summary || 'No summary available.',
        '',
        '## Key Points',
        '',
        keyPoints,
        '',
        '## Action Items',
        '',
        actionItems,
        '',
        '## Questions & Follow-ups',
        '',
        questions,
        '',
        '---',
        '',
        '## Raw Transcript',
        '',
        '<details>',
        '<summary>Click to expand full transcript</summary>',
        '',
        transcript,
        '',
        '</details>',
        ''
    ].join('\n');

    const binaryData = await this.helpers.prepareBinaryData(
        Buffer.from(markdown, 'utf8'),
        filename,
        'text/markdown'
    );

    results.push({
        json: { filename, filepath, archivePath, sourcePath: $('Set Data').first().json.path, title: llmData.title, status: 'processed' },
        binary: { data: binaryData }
    });
}

return results;

